import React from 'react';
import logo from './logo.svg';
import './App.css';
import * as texts from './texts.json';

function App() {
  const intros = texts[0];
  const middles = texts[1];
  const endings = texts[2];
  const introInd = Math.floor(Math.random()*intros.length);
  const middleInd = Math.floor(Math.random()*middles.length);
  const endingInd = Math.floor(Math.random()*endings.length);

  const selectedIntro = intros[introInd].replace('{0}','The Dark Knight Rises')

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
      <p>
          <div>
              <input type="text" value="שם הסרט"></input>
          </div>
          <div>
            {selectedIntro} 
            <br/>
            {middles[middleInd]}
            <br/>
            {endings[endingInd]}
          </div>
      </p>
    </div>
  );
}

export default App;
