import React from 'react';
import logo from './logo.svg';
import './App.css';
import * as texts from './texts.json';

function GenerateReview(movie){
  const intros = texts[0];
  const middles = texts[1];
  const endings = texts[2];
  const introInd = Math.floor(Math.random()*intros.length);
  const middleInd = Math.floor(Math.random()*middles.length);
  const endingInd = Math.floor(Math.random()*endings.length);

  const review = 
    intros[introInd].replace('{0}','The Dark Knight Rises') 
    + '<br/>'+ 
    middles[middleInd].replace('{0}','The Dark Knight Rises') 
    + '<br/>'+
    endings[endingInd].replace('{0}','The Dark Knight Rises') 

  return (review)
}

export class App extends React.Component{
  state = {
    movie: ''
  }

  onInputSubmit(e){
    this.setState({movie:e.target.value})
    console.log(e.target.value);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <p>
            <div>
                <input id='movieName' type="text" placeholder="שם הסרט" value={this.state.movie} onChange={this.onInputSubmit}></input>
                <button onClick={GenerateReview(this.state.movie)}>צור ביקורת</button>
            </div>
            {/* <div>
              {selectedIntro} 
              <br/>
              {middles[middleInd]}
              <br/>
              {endings[endingInd]}
            </div> */}
        </p>
      </div>
    );
  }
}

export default App;
